import { Component, OnInit } from '@angular/core';
import {FormControl, FormGroup, Validators} from "@angular/forms";
import {AuthService} from "../../services/auth.service";

@Component({
  selector: 'app-sign-in-card',
  templateUrl: './sign-in-card.component.html',
  styleUrls: ['./sign-in-card.component.css']
})
export class SignInCardComponent implements OnInit {

  constructor(private  authService:AuthService) { }

  form = new FormGroup({
    login: new FormControl<string>(""),
    password: new FormControl<string>("")
  })

  ngOnInit(): void {
  }

  async submit(){
    await this.authService.login({
      login: this.form.value['login'] as string,
      password: this.form.value['password'] as string
    })

  }

}
<!--Компонент авторизации пользователя-->

<div class="login">
  <div class="sign-in-card" [formGroup]="form" (ngSubmit)="submit()">
    <h1>Вход</h1>
    <input class="sing-in-input" placeholder="Логин" formControlName="login">
    <input type="password" class="sing-in-input" placeholder="Пароль" formControlName="password">
    <a href="" class="sing-in-password-recovery">Забыли пароль?</a>
    <div style="width: 100%; display: flex; align-content: center; justify-content: start ">
      <a class="glo" (click)="submit()" (keyup.enter)="submit()" routerLink="">Войти</a>
      <div style="margin: auto; "><a [routerLink]="'/account/register'" class="sing-in-password-recovery"><span
        style="color: black">или </span>Зарегистрироваться</a></div>
    </div>
  </div>
</div>
